package top.xcphoenix.algorithm.leetcode.day191129;

import com.alibaba.fastjson.JSON;

import java.util.*;

/**
 * @author xuanc
 * @version 1.0
 * @date 2019/11/29 下午9:23
 */
public class Solution {
    public int countServers(int[][] grid) {
        Set<Integer> indexSet = new HashSet<>();

        for (int i = 0; i < grid.length; i++) {
            int count = 0, index = -1;
            for (int j = 0; j < grid[i].length; j++) {
                if (grid[i][j] == 1) {
                    count++;
                    index = j;
                }
                if (count >= 2) {
                    break;
                }
            }
            if (count == 1) {
                indexSet.add(index);
            }
        }
        return 0;
    }

    public List<List<String>> suggestedProducts(String[] products, String searchWord) {
        Arrays.sort(products, String::compareTo);

        int cnt = 0;
        List<List<String>> result = new LinkedList<>();

        for (int i = 0; i < searchWord.length(); i++) {
            List<String> item = new LinkedList<>();
            while (cnt < products.length &&
                    (i >= products[cnt].length() || products[cnt].charAt(i) != searchWord.charAt(i))) {
                cnt++;
            }
            for (int j = cnt; j < products.length; j++) {
                if (i >= products[j].length()) {
                    continue;
                }
                if (products[j].charAt(i) == searchWord.charAt(i)) {
                    if (item)
                    item.add(products[j]);
                } else {
                    break;
                }
            }
            result.add(item);
        }
        return result;
    }

    public static void main(String[] args) {
        String[] products = new String[]{
                "","","",""
        };
        List<List<String>> lists = new Solution().suggestedProducts(products, "");
        System.out.println(JSON.toJSON(lists));
    }
}
